pragma solidity ^0.5.0;
contract Donation {

      // event Donated(address indexed donor, uint numDonors, uint amount);   事件：1，3选一个作为contribute的事件
      // emit Donated(msg.sender, donorID, msg.value);
      // event transfer(address indexed _from, address indexed _to, uint indexed value);


    address owner;// 合约拥有者的地址


    // 定义一个`Funder`结构体类型，用于表示捐赠人，其中有他的钱包地址和他一共出资的总额度。
    struct Funder {
        address addr; // 捐赠人地址
        uint amount;  // 捐赠总额
    }


   // 定义一个表示存储受捐人相关信息的结构体
    struct Donor {
        address beneficiary; // 受捐人钱包地址
        uint fundingGoal; // 需要筹集的总金额
        uint numFunders; // 捐赠人的id
        uint amount; // 已赞助的总金额
        mapping (uint => Funder) funders; // 映射，将捐赠者的id与捐赠者绑定在一起，从而能够得知，是谁给当前的受捐人捐钱了。
    }


    uint numDonors; // 受捐人的id数
    mapping  (uint => Donor) donors; // 同样以映射的方式存储被受捐人的信息


    modifier onlyOwner() {
        require(owner == msg.sender);
        _;          // 函数装饰器--合约拥有者权限
    }

    constructor() public  {
        owner = msg.sender;     // 合约构造函数
}


    // 新增一个`Donor`对象，需要传入受捐人的地址和需要筹资的总额
    function newDonor (address _beneficiary, uint _goal) public{
        numDonors++; // 
        donors[numDonors] = Donor(_beneficiary, _goal, 0, 0); // 
    }
// 通过donorID给某个Donor对象捐助
    function contribute (address _addr, uint _numDonors) public payable  {
        Donor storage c = donors[_numDonors];//通过_numDonors获取对应的Donor对象
        if (c.amount  <  c.fundingGoal) {
         c.amount += msg.value; // 计算收到的总金额 //msg.value就是给当前合约转账的金额
         c.numFunders++;// 捐赠人数增加
         c.funders[c.numFunders] = Funder(_addr, msg.value); // 存储捐赠人信息


    }
    }
    // 查看合约余额
    function getBalance() view public returns(uint) {
        return address(this).balance;    
    }

    //根据受捐人id查看当前已收到的捐款金额
    function getDonorAmount(uint _numDonors) view public returns(uint) {
        Donor storage c = donors[_numDonors];//通过_numDonors获取对应的Donor对象
        return c.amount;   
    }
        //根据受捐人id查看受捐人地址已收到的捐款金额
    function getDonorBalance(uint _numDonors) view public returns(uint) {
        Donor storage c = donors[_numDonors];//通过_numDonors获取对应的Donor对象
        return address(c.beneficiary).balance;   
    }

 // 当证据经过审核，机构就会通过智能合约给受捐人的地址转账,transferAmount为转账金额
    function isComplete (uint _numDonors,uint transferAmount) public payable {
        Donor storage c = donors[_numDonors];
        if (transferAmount  <=  c.amount) {
            address(uint160(c.beneficiary)).transfer(transferAmount);
            c.amount-=transferAmount;
}
}
}
